


import React, { useState, useEffect } from 'react';
const url = 'https://indapi.kumba.io/webdev/assignment';
const MultipleReturns = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);
  const [userOrder, setUserOrder] = useState();



  useEffect(() => {
    fetch(url)
      .then((resp) => {
        if (resp.status >= 200 && resp.status <= 299) {
          return resp.json();
        } else {
          setIsLoading(false);
          setIsError(true);
          throw new Error(resp.statusText);
        }
      })
      .then((userOrder) => {
        const { user } = userOrder;
        setUserOrder(user);
        setIsLoading(false);
      })
      .catch((error) => console.log(error));
  }, []);

  if (isLoading) {
    return (
      <div>
        <h1>Loading...</h1>
      </div>
    );
  }
  if (isError) {
    return (
      <div>
        <h1>Error....</h1>
      </div>
    );
  }
const {name,id,address,phone,about,likes,dislikes} = userOrder;
const img = 'data:image/jpeg;base64,'; 
  return (

        <div className = "card">
          <div className="card-body">
            <img className= "img-fluid rounded mx-auto d-block" src={img}/>


            <p className="card-title mt-5"><b>Name:</b> {name}</p>
              <p className="card-text"><b>Id:</b> {id}</p>
              <p><b>Address:</b> {address}</p>
              <p><b>Phone:</b> {phone}</p>
              <p><b>About:</b> {about}</p>

              <p><b>Likes:</b> {likes.join(',')}</p>
              <p><b>Dislikes:</b> {dislikes.join(',')}</p>
          </div>
          
        </div>

  );
};

export default MultipleReturns;
